    /** @private */
    updateAnnotationDrag(node, diagram, tx, ty) {
        let checkAnnotation = node.id.split('_');
        if (checkAnnotation[1] === 'textannotation') {
            let parentNode;
            for (let j = 0; j < node.inEdges.length; j++) {
                let connector = diagram.nameTable[node.inEdges[j]];
                if (connector) {
                    parentNode = diagram.nameTable[connector.sourceID];
                }
                let start = { x: node.offsetX + tx, y: node.offsetY + ty };
                let end = connector.sourcePoint;
                let length = Point.findLength(start, end);
                let angle = Point.findAngle(end, start);
                if (parentNode.shape.annotations) {
                    for (let x = 0; x < (parentNode.shape.annotations).length; x++) {
                        if ((parentNode.shape.annotations)[x].id === checkAnnotation[checkAnnotation.length - 1]) {
                            (parentNode.shape.annotations[x]).length = length;
                            (parentNode.shape.annotations[x]).angle = angle;
                            this.setAnnotationPath(parentNode.wrapper.bounds, node.wrapper, start, node);
                            return false;
                        }
                    }
                }
            }
        }
        if (node.processId) {
            this.drag(node, tx, ty, diagram);
            return true;
        }
        return false;
    }
